package com.ly666.ahead.config;import com.baomidou.mybatisplus.extension.plugins.PaginationInterceptor;import org.mybatis.spring.annotation.MapperScan;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;import org.springframework.transaction.annotation.EnableTransactionManagement;/** * @author ：i18n * @date ：Created in 2019/4/21 19:25 * @desc : {} */@EnableTransactionManagement@Configuration@MapperScan("com.ly666.ahead.mapper")public class MybatisConfig {	/**	 * 分页插件	 */	@Bean	public PaginationInterceptor paginationInterceptor() {		return new PaginationInterceptor();	}}