package com.ly666.ahead.controller;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.RequestMapping;/** * @author ：lxwnb * @date ：Created in 2019/6/4 12:29 * @desc : {} */@Controller@RequestMapping("url")public class UrlController {	@RequestMapping("{module}/{name}")	public String url1(@PathVariable String module,@PathVariable String name){		return module+"/"+name;	}	@RequestMapping("{name}")	public String url2(@PathVariable String name){		return name;	}	@RequestMapping("{module}/{module2}/{name}")	public String url3(@PathVariable String module,@PathVariable String module2,@PathVariable String name){		return module+"/"+module2+"/"+name;	}}