package com.ly666.ahead.controller.home;import com.ly666.ahead.entity.NoticeInfo;import com.ly666.ahead.service.INoticeInfoService;import org.springframework.stereotype.Controller;import org.springframework.ui.Model;import org.springframework.web.bind.annotation.RequestMapping;import java.util.List;/** * @author ：luoyi * @date ：Created in 2019/5/31 20:47 * @desc : {} */@Controllerpublic class HomeController {	private INoticeInfoService iNoticeInfoService;	public HomeController(final INoticeInfoService iNoticeInfoService) {		this.iNoticeInfoService = iNoticeInfoService;	}	@RequestMapping("/")	public String home(Model model){		List<NoticeInfo> list = iNoticeInfoService.list().subList(0,4);		model.addAttribute("noticInfos", list);		return "home";	}}