package com.ly666.ahead.global.interceptor;import lombok.extern.slf4j.Slf4j;import org.springframework.lang.Nullable;import org.springframework.web.servlet.HandlerInterceptor;import org.springframework.web.servlet.ModelAndView;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;/** * @author ：luoyi * @date ：Created in 2019/4/22 8:16 * @desc : {全局拦截器} */@Slf4jpublic class GlobalInterceptor implements HandlerInterceptor {	public boolean preHandle(HttpServletRequest request, HttpServletResponse response, Object handler) throws Exception {		return true;	}	public void postHandle(HttpServletRequest request, HttpServletResponse response, Object handler, @Nullable ModelAndView modelAndView) throws Exception {	}	public void afterCompletion(HttpServletRequest request, HttpServletResponse response, Object handler, @Nullable Exception ex) throws Exception {	}}