package com.ly666.ahead.model.jwt;import io.jsonwebtoken.Claims;import lombok.Getter;import lombok.Setter;/** * @author ：luoyi * @date ：Created in 2019/5/13 20:16 * @desc : {封装的一个jwt认证过程中的结果集，它包含了是否认证成功，错误状态码，成功认证数据} */@Getter@Setterpublic class JwtResult {	/**	 * 是否成功，代表结果的状态	 * */	private Boolean success;	/**	* 错误编码，在JWTUtils中定义的常量	* */	private Integer errCode;	/**	* 验证过程中payload中的数据	* */	private Claims claims;	public JwtResult(){}	/*	* 成功	* */	public JwtResult(final Boolean success, final Claims claims) {		this.success = success;		this.claims = claims;	}	/*	 * 失败	 * */	public JwtResult(final Boolean success, final Integer errCode) {		this.success = success;		this.errCode = errCode;	}}