package com.ly666.ahead.security.util;import com.ly666.ahead.global.defaultValue.GlobalProperties;import lombok.extern.slf4j.Slf4j;import org.apache.shiro.crypto.hash.Md5Hash;import org.springframework.stereotype.Service;/** * @author ：luoyi * @date ：Created in 2019/5/13 20:24 * @desc : {签名/加密/摘要服务工具类} */@Slf4j@Servicepublic class SecurityUtil {	/**	 * shiro密码加密工具类	 *	 * @param credentials 密码	 * @return   返回加密后的新密码	 */	public static String generaPassword(String credentials) {		return new Md5Hash(credentials).toString();	}	/**	 * shiro密码加密工具类	 *	 * @param credentials 密码	 * @param saltSource  密码盐	 * @return   返回加密后的新密码	 */	public static String generaPassword(String credentials, String saltSource) {		return new Md5Hash(credentials, saltSource + GlobalProperties.HASH_SALT, GlobalProperties.HASH_ITERATIONS).toString();	}}