package com.ly666.project.global.interceptor;import lombok.extern.slf4j.Slf4j;import org.springframework.web.servlet.HandlerInterceptor;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;/** * @author ：luoyi * @date ：Created in 2019/4/22 8:16 * @desc : {全局拦截器} */@Slf4jpublic class GlobalInterceptor implements HandlerInterceptor {	public boolean preHandle(HttpServletRequest request, HttpServletResponse response, Object handler) throws Exception {		return true;	}}